import React, { useRef, useState } from 'react';
import { useFrame } from '@react-three/fiber';
import { Box, Plane, RoundedBox } from '@react-three/drei';

const ThreeDLaptop = () => {
  const laptopRef = useRef();
  const screenRef = useRef();
  const [hovered, setHovered] = useState(false);

  useFrame((state) => {
    if (laptopRef.current) {
      laptopRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.5) * 0.1;
      laptopRef.current.position.y = Math.sin(state.clock.elapsedTime * 0.8) * 0.1;
    }
    
    if (screenRef.current && hovered) {
      screenRef.current.material.emissiveIntensity = Math.sin(state.clock.elapsedTime * 2) * 0.3 + 0.7;
    }
  });

  return (
    <group 
      ref={laptopRef}
      scale={hovered ? 1.1 : 1}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
    >
      {/* Laptop Base */}
      <RoundedBox
        args={[4, 0.2, 3]}
        position={[0, -0.1, 0]}
        radius={0.1}
        smoothness={4}
      >
        <meshStandardMaterial 
          color="#2d3748"
          metalness={0.8}
          roughness={0.2}
        />
      </RoundedBox>

      {/* Screen */}
      <group position={[0, 1.2, -1.2]} rotation={[-0.2, 0, 0]}>
        {/* Screen Frame */}
        <RoundedBox
          args={[3.8, 2.4, 0.1]}
          position={[0, 0, -0.05]}
          radius={0.1}
          smoothness={4}
        >
          <meshStandardMaterial 
            color="#1a202c"
            metalness={0.9}
            roughness={0.1}
          />
        </RoundedBox>

        {/* Screen Display */}
        <Plane 
          ref={screenRef}
          args={[3.4, 2.0]}
          position={[0, 0, 0]}
        >
          <meshStandardMaterial
            color="#000020"
            emissive="#6366f1"
            emissiveIntensity={0.5}
            transparent
            opacity={0.9}
          />
        </Plane>

        {/* Screen Glow Effect */}
        <Plane args={[4, 2.6]} position={[0, 0, -0.01]}>
          <meshBasicMaterial
            color="#6366f1"
            transparent
            opacity={0.1}
          />
        </Plane>
      </group>

      {/* Keyboard */}
      <Box
        args={[3.2, 0.05, 2.2]}
        position={[0, 0.05, 0.2]}
      >
        <meshStandardMaterial 
          color="#4a5568"
          roughness={0.7}
        />
      </Box>

      {/* Trackpad */}
      <RoundedBox
        args={[1.2, 0.02, 0.8]}
        position={[0, 0.08, 0.8]}
        radius={0.05}
        smoothness={4}
      >
        <meshStandardMaterial 
          color="#2d3748"
          roughness={0.3}
          metalness={0.5}
        />
      </RoundedBox>

      {/* Ambient Lighting Effect */}
      <pointLight
        position={[0, 2, 1]}
        color="#6366f1"
        intensity={0.5}
        distance={10}
      />
    </group>
  );
};

export default ThreeDLaptop;
